<?include_once("api.watermark.php");$thisF = dirname(__FILE__)."/22.png";$wtM = $_SERVER["DOCUMENT_ROOT"]."/upload/import/default/watermark.png";$wt = new watermark();$img = imagecreatefrompng($thisF);imagealphablending($img, true);imagesavealpha($img, true);$width = imagesx($img);    $height = imagesy($img); $trans = imagecolorallocatealpha($img, 0, 0, 0, 127);    for ($x = 0; $x < $width; $x++)    {        for ($y = 0; $y < $height; $y++)        {            imagesetpixel($img, $x, $y, $trans);        }    }$img2 = imagecreatefrompng($wtM);$im = $wt->create_watermark($img,$img2,50);header("Content-type: image/png");imagejpeg($im);?>